'use strict';

jest.dontMock('../../app/js/lib/validate');

var Validate = require('../../app/js/lib/validate');

describe('validate rejection', function () {
  //it('throws error on no arguments', () => {
  //  var undefinedVar
  //  expect(Validate.hasArguments(undefinedVar)).toBe(false)
  //})

  it('throws error on invalid date', function () {
    expect(function () {Validate.date({ month: 13, date: 1, year: 2000 });}).toThrow('argument out of range');
    expect(function () {Validate.date({ month: 11, date: 32, year: 2000 });}).toThrow('argument out of range');
    expect(function () {Validate.date({ month: 11, date: 1, year: 10000 });}).toThrow('argument out of range');

    expect(function () {Validate.date({ month: 11, date: 1 });}).toThrow('empty arguments');
    expect(function () {Validate.date({ year: 1996 });}).toThrow('empty arguments');});});